CREATE OR REPLACE FUNCTION obter_codigo_cbo_tiss_vigente ( nr_seq_cbo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_retorno_w			varchar(15);


BEGIN

select	coalesce(max(a.cd_cbo),'')
into STRICT	cd_retorno_w
FROM cbo_saude a
LEFT OUTER JOIN cbo_saude_tiss b ON (a.nr_sequencia = b.nr_seq_cbo_saude)
WHERE a.nr_sequencia	= nr_seq_cbo_p  and coalesce(a.ie_situacao,'A') = 'A' and (coalesce(b.ie_versao::text, '') = '' or b.ie_versao = pls_obter_versao_tiss);

return cd_retorno_w;

end;
