CREATE OR REPLACE FUNCTION obter_codigo_darf_tit_imposto (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_cgc_w		varchar(14);
cd_pessoa_fisica_w	varchar(10);
cd_tributo_w		bigint;
cd_estabelecimento_w	smallint;

cd_darf_w		varchar(30);


BEGIN 
 
select	b.cd_estabelecimento, 
	a.cd_tributo, 
	b.cd_pessoa_fisica, 
	b.cd_cgc 
into STRICT	cd_estabelecimento_w, 
	cd_tributo_w, 
	cd_pessoa_fisica_w, 
	cd_cgc_w 
from	titulo_pagar b, 
	titulo_pagar_imposto a 
where	a.nr_titulo	= b.nr_titulo 
and	a.nr_sequencia	= nr_sequencia_p;
 
cd_darf_w	:= obter_codigo_darf(cd_tributo_w, 
				cd_estabelecimento_w, 
				cd_cgc_w, 
				cd_pessoa_fisica_w);
		 
 
return	cd_darf_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
