SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_codigo_descricao_etapa (nr_interno_conta_p bigint, nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		    varchar(150);
nr_sequencia_w          bigint;
nr_seq_etapa_w          bigint;
ds_etapa_w              varchar(150);



BEGIN

ds_retorno_w := '';

SELECT	coalesce(MAX(nr_sequencia),0)
INTO STRICT 	nr_sequencia_w
FROM	conta_paciente_etapa
WHERE   nr_interno_conta  = nr_interno_conta_p
AND     nr_sequencia < nr_sequencia_p;

if (nr_sequencia_w > 0) then

	SELECT	coalesce(max(nr_seq_etapa),0)
	INTO STRICT 	nr_seq_etapa_w
	FROM	conta_paciente_etapa
	WHERE   nr_sequencia  = nr_sequencia_w;

	ds_retorno_w := nr_seq_etapa_w;
end if;

if (ie_opcao_p = 'D') then

	SELECT	max(ds_etapa)
	INTO STRICT	ds_etapa_w
	FROM	fatur_etapa
	WHERE	nr_sequencia = nr_seq_etapa_w;

	ds_retorno_w := ds_etapa_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
