CREATE OR REPLACE FUNCTION obter_codigo_descricao_tuss ( nr_seq_proc_interno_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_tuss_w	bigint;
ds_procedimento_tuss_w	varchar(255);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(612231, null, wheb_usuario_pck.get_nr_seq_idioma);--NÃ£o encontrado
BEGIN

select 	substr(obter_descricao_procedimento(max(cd_procedimento_tuss),8),1,200),
	max(cd_procedimento_tuss)
into STRICT	ds_procedimento_tuss_w,
	cd_procedimento_tuss_w
from 	proc_interno
where 	nr_sequencia = nr_seq_proc_interno_p;

if (coalesce(cd_procedimento_tuss_w,0) > 0) then
	return 	cd_procedimento_tuss_w ||' - '||ds_procedimento_tuss_w;
else

	return	expressao1_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
