CREATE OR REPLACE FUNCTION obter_codigo_dpc (nr_atendimento_p bigint, dt_referencia_p patient_dpc.dt_start_dpc%type default null) RETURNS varchar AS $body$
DECLARE

					
cd_dpc_w varchar(300);


BEGIN
select max(b.cd_dpc || '-' || b.nm_dpc)
into STRICT cd_dpc_w
from patient_dpc a, dpc_score b
where b.nr_sequencia = a.nr_seq_dpc_score
and a.nr_atendimento = nr_atendimento_p
and (coalesce(dt_referencia_p::text, '') = '' or (trunc(dt_referencia_p,'dd') between trunc(a.dt_start_dpc,'dd') and coalesce(trunc(a.dt_end_dpc,'dd'),trunc(dt_referencia_p,'dd'))));

if coalesce(cd_dpc_w::text, '') = '' then
     cd_dpc_w := obter_desc_expressao(1069798);
end if;

return cd_dpc_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
