CREATE OR REPLACE FUNCTION obter_codigo_fat_301 ( nr_seq_proc_pac_p bigint) RETURNS varchar AS $body$
DECLARE


cd_completo_w			varchar(8)	:= null;
cd_departamento_w		departamento_medico.cd_departamento%type;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;

c01 CURSOR FOR
SELECT	a.nr_seq_301_codigo_internado nr_sequencia,
	a.cd_procedimento,
	a.ie_origem_proced
from	c301_regra_codigo_fat a
where	a.cd_estabelecimento	= cd_estabelecimento_w
and	a.cd_procedimento	= cd_procedimento_w
and	a.ie_origem_proced	= ie_origem_proced_w
and (a.cd_departamento = cd_departamento_w or coalesce(a.cd_departamento::text, '') = '')
order by
		coalesce(a.cd_departamento,0);

c01_w	c01%rowtype;


BEGIN

if (nr_seq_proc_pac_p IS NOT NULL AND nr_seq_proc_pac_p::text <> '') then
	select	obter_departamento_data(a.nr_atendimento,clock_timestamp()),
		a.cd_procedimento,
		a.ie_origem_proced,
		b.cd_estabelecimento
	into STRICT	cd_departamento_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		cd_estabelecimento_w
	from	atendimento_paciente b,
		procedimento_paciente a
	where	a.nr_atendimento = b.nr_atendimento
	and	a.nr_sequencia	= nr_seq_proc_pac_p;

	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		select	max(a.cd_completo)
		into STRICT	cd_completo_w
		from	c301_4_codigo_internado a
		where	a.nr_sequencia	= c01_w.nr_sequencia;
	end loop;
	close C01;

end if;

return	cd_completo_w;

end;
$body$
LANGUAGE PLPGSQL
