CREATE OR REPLACE FUNCTION obter_codigo_int_integ_cli ( nm_atributo_p text, ie_tipo_pessoa_p text, cd_externo_p text) RETURNS varchar AS $body$
DECLARE


cd_retorno_w		varchar(30);
qt_existe_w		bigint;


BEGIN
select	count(1)
into STRICT	qt_existe_w
from	w_importa_cliente_depara
where	((ie_tipo_pessoa = ie_tipo_pessoa_p) or (ie_tipo_pessoa = 'T'))
and	nm_atributo = nm_atributo_p
and	cd_externo = cd_externo_p;

if (qt_existe_w > 0) then
	select	max(cd_interno)
	into STRICT	cd_retorno_w
	from	w_importa_cliente_depara
	where	((ie_tipo_pessoa = ie_tipo_pessoa_p) or (ie_tipo_pessoa = 'T'))
	and	nm_atributo = nm_atributo_p
	and	cd_externo = cd_externo_p;

else
	cd_retorno_w	:= cd_externo_p;
end if;

return cd_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
