CREATE OR REPLACE FUNCTION obter_codigo_metodo_pago (cd_condicao_pagamento_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(255);
ie_forma_pagamento_w	 condicao_pagamento.ie_forma_pagamento%type;

BEGIN

select 	coalesce(ie_forma_pagamento,0)
into STRICT	ie_forma_pagamento_w
from	condicao_pagamento
where	cd_condicao_pagamento = cd_condicao_pagamento_p;

if (ie_forma_pagamento_w = 1)then
	ds_retorno_w	:=	'S';
else
	ds_retorno_w	:=	'N';

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
