CREATE OR REPLACE FUNCTION obter_codigo_pacote_acomod (nr_seq_pacote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_procedimento_w	bigint;

C01 CURSOR FOR
	SELECT	distinct cd_procedimento
	from	pacote_tipo_acomodacao
	where	nr_seq_pacote = nr_seq_pacote_p
	order by cd_procedimento;


BEGIN

ds_retorno_w:= '';

open C01;
loop
fetch C01 into
	cd_procedimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w:=  substr(ds_retorno_w || cd_procedimento_w || '; ',1,255);

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
