CREATE OR REPLACE FUNCTION obter_codigo_proced_tiss (ie_proc_tuss_p text, cd_procedimento_tuss_p text, cd_proc_conv_p text, ie_proc_pacote_p text, cd_procedimento_p text, cd_proc_regra_tabela_p text) RETURNS varchar AS $body$
DECLARE

	
cd_proc_tiss_w	varchar(255);

BEGIN


if	(cd_proc_regra_tabela_p IS NOT NULL AND cd_proc_regra_tabela_p::text <> '')  then

	cd_proc_tiss_w := cd_proc_regra_tabela_p;
	
elsif (ie_proc_tuss_p = 'S') and (coalesce(cd_procedimento_tuss_p,'0') <> '0') then
	cd_proc_tiss_w	:= coalesce(cd_procedimento_tuss_p,cd_proc_conv_p);
elsif (ie_proc_tuss_p = 'P') and (ie_proc_pacote_p <> 'S') then
	cd_proc_tiss_w	:= coalesce(cd_procedimento_tuss_p,cd_proc_conv_p);
elsif (ie_proc_tuss_p	= 'C') then
	if (coalesce(cd_proc_conv_p,'0') <> '0') and (cd_proc_conv_p <> cd_procedimento_p) then --Conversao
		cd_proc_tiss_w	:= cd_proc_conv_p;
	elsif	(((coalesce(cd_proc_conv_p,'0') = '0') or (cd_proc_conv_p = cd_procedimento_p)) and --Proc TUSS
		 (coalesce(cd_procedimento_tuss_p,'0') <> '0'))  then
		cd_proc_tiss_w	:= cd_procedimento_tuss_p;
	elsif	((coalesce(cd_procedimento_tuss_p,'0') = '0') and --Sem Conversao e Sem Proc TUSS
		 (cd_proc_conv_p = cd_procedimento_p)) then
		cd_proc_tiss_w	:=  cd_procedimento_p;
	end if;

	
end if;	

cd_proc_tiss_w		:= coalesce(cd_proc_tiss_w, cd_procedimento_p);
cd_proc_tiss_w		:= replace(replace(cd_proc_tiss_w, '.',''),'-','');

return cd_proc_tiss_w;
end;
