CREATE OR REPLACE FUNCTION obter_codigo_retencao ( cd_darf_p text, cd_tributo_p bigint, cd_estabelecimento_p bigint, cd_cgc_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
cd_darf_w		varchar(10);


BEGIN 
 
cd_darf_w := cd_darf_p;
 
if (coalesce(cd_darf_w::text, '') = '') then 
	 
	cd_darf_w := SUBSTR(obter_codigo_darf(cd_tributo_p, cd_estabelecimento_p, cd_cgc_p, cd_pessoa_fisica_p),1,10);
 
	if (coalesce(cd_darf_w::text, '') = '') then 
	 
		if (coalesce(cd_pessoa_fisica_p::text, '') = '') then 
			cd_darf_w := '1708';
		else 
			cd_darf_w := '0588';
		end if;
	end if;
	 
end if;
 
return	cd_darf_w;
 
end;
