CREATE OR REPLACE FUNCTION obter_codigo_simpro (cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


cd_simpro_w		bigint 	:= 0;


BEGIN

select max(cd_simpro)
into STRICT	cd_simpro_w
from	material_simpro
where	cd_material	= cd_material_p
and		ie_situacao 	= 'A';

return cd_simpro_w;

END;
