SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cod_agenda_ageint (nr_seq_ageint_item_p bigint) RETURNS bigint AS $body$
DECLARE


cd_agenda_w		bigint;
ie_tipo_agendamento_w		agenda_integrada_item.ie_tipo_agendamento%type;
nr_seq_cons_serv_w			agenda_integrada_item.nr_seq_agenda_cons%type;
nr_seq_exame_w				agenda_integrada_item.nr_seq_agenda_exame%type;
nr_seq_quimio_w				agenda_integrada_item.nr_seq_agequi%type;


BEGIN
select	max(ie_tipo_agendamento),
		max(nr_seq_agenda_cons),
		max(nr_seq_agenda_exame),
		max(nr_seq_agequi)
into STRICT	ie_tipo_agendamento_w,
		nr_seq_cons_serv_w,
		nr_seq_exame_w,
		nr_seq_quimio_w
from	agenda_integrada_item
where	nr_sequencia = nr_seq_ageint_item_p;

if (ie_tipo_agendamento_w = 'C') or (ie_tipo_agendamento_w = 'S') then -- Consulta / Servico
	select	max(cd_agenda)
	into STRICT	cd_agenda_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_cons_serv_w;
elsif (ie_tipo_agendamento_w = 'E') then -- Exames
	select	max(cd_agenda)
	into STRICT	cd_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_exame_w;
elsif (ie_tipo_agendamento_w = 'Q') then --Quimio
	select	max(nr_seq_local)
	into STRICT	cd_agenda_w
	from	agenda_quimio
	where	nr_sequencia = nr_seq_quimio_w;
end if;

return cd_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
