SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cod_diagnostico_epis_ger ( nr_seq_episodio_p bigint) RETURNS varchar AS $body$
DECLARE


dt_diagnostico_w		timestamp;
ds_diagnostico_w		varchar(10);


BEGIN

begin
select	max(dt_diagnostico)
into STRICT 	dt_diagnostico_w
from 	diagnostico_medico a
where	nr_atendimento in (	SELECT 	a.nr_atendimento
					from 	atendimento_paciente a,
							episodio_paciente b
					where 	a.nr_seq_episodio = b.nr_sequencia
					and 	b.nr_sequencia = nr_seq_episodio_p)
and	exists (	select	1
			from	diagnostico_doenca x
			where	x.nr_atendimento in (	select 	y.nr_atendimento
									from 	atendimento_paciente y,
											episodio_paciente z
									where 	y.nr_seq_episodio = z.nr_sequencia
									and 	z.nr_sequencia = nr_seq_episodio_p)
			and		x.dt_diagnostico = a.dt_diagnostico
			and		x.ie_classificacao_doenca = 'P');
exception
	when no_data_found then
		dt_diagnostico_w := null;
end;



if (dt_diagnostico_w IS NOT NULL AND dt_diagnostico_w::text <> '') then
	select	 coalesce(max(cd_doenca_cid),null)
	into STRICT	ds_diagnostico_w
	from 	cid_doenca
	where	cd_doenca_cid = (	SELECT max(cd_doenca)
					from	diagnostico_doenca a
					where	a.nr_atendimento in (	select 	a.nr_atendimento
												from 	atendimento_paciente a,
														episodio_paciente b
												where 	a.nr_seq_episodio = b.nr_sequencia
												and 	b.nr_sequencia = nr_seq_episodio_p)
					  and	a.dt_diagnostico = dt_diagnostico_w
					  and	a.ie_classificacao_doenca = 'P');
end if;

return ds_diagnostico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
