CREATE OR REPLACE FUNCTION obter_cod_ext_material (cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_sistema_externo_w		varchar(255);


BEGIN

select	max(cd_sistema_externo)
into STRICT	cd_sistema_externo_w
from	conv_mat_sistema_externo
where	cd_material = cd_material_p
and	cd_estabelecimento = cd_estabelecimento_p
and	((clock_timestamp() between dt_inicio_vigencia and dt_fim_vigencia) or (coalesce(dt_inicio_vigencia::text, '') = '' or coalesce(dt_fim_vigencia::text, '') = ''));

return	cd_sistema_externo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
