CREATE OR REPLACE FUNCTION obter_cod_fornec_int_compras ( cd_fornecedor_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_cod_fornec_integracao_w		parametro_compras.ie_cod_fornec_integracao%type;			
cd_fornecedor_w				varchar(255);


BEGIN 
 
select	coalesce(max(ie_cod_fornec_integracao),'CNPJ') 
into STRICT	ie_cod_fornec_integracao_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_cod_fornec_integracao_w = 'CNPJ') then 
	select	obter_cgc_cpf_editado(cd_fornecedor_p) 
	into STRICT	cd_fornecedor_w 
	;
elsif (ie_cod_fornec_integracao_w = 'RFC') then 
	select	cd_rfc 
	into STRICT	cd_fornecedor_w 
	from	pessoa_juridica 
	where	cd_cgc = cd_fornecedor_p;
elsif (ie_cod_fornec_integracao_w = 'CURP') then 
	select	cd_curp 
	into STRICT	cd_fornecedor_w 
	from	pessoa_juridica 
	where	cd_cgc = cd_fornecedor_p;
end if;
 
return	cd_fornecedor_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
