CREATE OR REPLACE FUNCTION obter_cod_int_material ( cd_sistema_externo_p text, ie_sistema_externo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w		bigint;


BEGIN

select	max(cd_material)
into STRICT	cd_material_w
from	conv_mat_sistema_externo
where	cd_sistema_externo = cd_sistema_externo_p
and	ie_sistema = ie_sistema_externo_p
and	cd_estabelecimento = cd_estabelecimento_p
and	((clock_timestamp() between dt_inicio_vigencia and dt_fim_vigencia) or (coalesce(dt_inicio_vigencia::text, '') = '' or coalesce(dt_fim_vigencia::text, '') = ''));

return	cd_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
