CREATE OR REPLACE FUNCTION obter_cod_item_processo_adep (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_material_w	integer;


BEGIN 
 
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then 
 
	select	(obter_dados_horario(nr_seq_horario_p,'CI'))::numeric  
	into STRICT	cd_material_w 
	;
 
elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then 
 
	select	(obter_dados_material_prescr(nr_prescricao_p,nr_seq_material_p,'CD_MATERIAL'))::numeric  
	into STRICT	cd_material_w 
	;
 
end if;
 
return cd_material_w;
 
end;
