CREATE OR REPLACE FUNCTION obter_cod_loinc ( nr_seq_prescr_p bigint, nr_prescricao_p bigint , ie_tipo_result_p text) RETURNS varchar AS $body$
DECLARE

ds_result_w		varchar(200);


BEGIN
    if (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '' AND nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
        select CASE WHEN ie_tipo_result_p='C' THEN  cd_loinc  ELSE ds_componente END
        into STRICT
        ds_result_w
        from lab_loinc_dados a,
        lab_loinc_dados_reg b,
        prescr_procedimento_loinc c
        where c.nr_seq_loinc_dados = a.nr_sequencia
        and b.nr_seq_loinc_dados = a.nr_sequencia
        and c.nr_seq_prescr = nr_seq_prescr_p
        and c.nr_prescricao = nr_prescricao_p;
    end if;
return ds_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
