CREATE OR REPLACE FUNCTION obter_cod_material_barra ( cd_barra_p text, cd_cgc_p text) RETURNS bigint AS $body$
DECLARE


cd_material_w		integer;


BEGIN

select	coalesce(max(cd_material),null)
into STRICT	cd_material_w
from	material_cod_barra
where	cd_cgc_fabricante = cd_cgc_p
and	cd_barra_material = cd_barra_p;

if (coalesce(cd_material_w::text, '') = '') then
	begin
	select	coalesce(max(cd_material),null)
	into STRICT	cd_material_w
	from	material_cod_barra
	where	coalesce(cd_cgc_fabricante::text, '') = ''
	and	cd_barra_material = cd_barra_p;
	end;
end if;

RETURN	cd_material_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
