CREATE OR REPLACE FUNCTION obter_cod_proc_interno_conv2 (nr_seq_proc_interno_p bigint, cd_convenio_p bigint, cd_categoria_p bigint, dt_vigencia_p timestamp, cd_estabelecimento_p bigint, ie_opcao_p bigint default 1) RETURNS bigint AS $body$
DECLARE

/*
ie_opcao_p
1 - procedimento
2 - proc interno
 */
cd_edicao_amb_w		integer;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
ie_prioridade_edicao_w	varchar(1);
dt_vigencia_w		timestamp;
ie_origem_proc_filtro_w	bigint;


BEGIN

dt_vigencia_w	:= trunc(coalesce(dt_vigencia_p, clock_timestamp()));

select	obter_edicao_amb2(cd_estabelecimento_p, cd_convenio_p, cd_categoria_p, dt_vigencia_p)
into STRICT	cd_edicao_amb_w
;

ie_origem_proc_filtro_w	:=	obter_origem_proced_cat(cd_estabelecimento_p, null, obter_tipo_convenio(cd_convenio_p), cd_convenio_p, cd_categoria_p);

select	coalesce(max(a.cd_procedimento),0),
	coalesce(max(a.ie_origem_proced),0)
into STRICT	cd_procedimento_w,
	ie_origem_proced_w
from	proc_interno_conv a
where	a.nr_seq_proc_interno 			= nr_seq_proc_interno_p
and	coalesce(a.cd_convenio, cd_convenio_p)	= cd_convenio_p
and	coalesce(a.cd_edicao_amb, coalesce(cd_edicao_amb_w,'0'))	= coalesce(cd_edicao_amb_w,'0')
and	coalesce(a.ie_origem_proc_filtro, coalesce(ie_origem_proc_filtro_w,'0'))	= coalesce(ie_origem_proc_filtro_w,'0')
and	dt_vigencia_w between coalesce(a.dt_inicio_vigencia, dt_vigencia_w) and coalesce(a.dt_final_vigencia, dt_vigencia_w);

if (ie_opcao_p = 1) then
	if (cd_procedimento_w = 0) then
		select	b.cd_procedimento
		into STRICT	cd_procedimento_w
		from	proc_interno b
		where	b.nr_sequencia = nr_seq_proc_interno_p;
	end if;

	return	cd_procedimento_w;

elsif (ie_opcao_p = 2) then
	if (ie_origem_proced_w = 0) then
		select	b.ie_origem_proced
		into STRICT	ie_origem_proced_w
		from	proc_interno b
		where	b.nr_sequencia = nr_seq_proc_interno_p;
	end if;

	return	ie_origem_proced_w;
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
