CREATE OR REPLACE FUNCTION obter_cod_proc_interno_conv (nr_seq_proc_interno_p bigint, cd_convenio_p bigint, cd_categoria_p bigint, dt_vigencia_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_edicao_amb_w		integer;
cd_procedimento_w	bigint;
dt_vigencia_w		timestamp;


BEGIN

dt_vigencia_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_vigencia_p, clock_timestamp()));

select	obter_edicao_amb(cd_estabelecimento_p, cd_convenio_p, cd_categoria_p, dt_vigencia_p)
into STRICT	cd_edicao_amb_w 
;
 
select	coalesce(max(a.cd_procedimento),0) 
into STRICT	cd_procedimento_w 
from	proc_interno_conv a 
where	a.nr_seq_proc_interno 			= nr_seq_proc_interno_p 
and	coalesce(a.cd_convenio, cd_convenio_p)	= cd_convenio_p 
and	coalesce(a.cd_edicao_amb, coalesce(cd_edicao_amb_w,0))	= coalesce(cd_edicao_amb_w,0)
and	dt_vigencia_w between coalesce(a.dt_inicio_vigencia, dt_vigencia_w) and coalesce(a.dt_final_vigencia, dt_vigencia_w);


if (cd_procedimento_w = 0) then 
	select	b.cd_procedimento 
	into STRICT	cd_procedimento_w 
	from	proc_interno b 
	where	b.nr_sequencia = nr_seq_proc_interno_p;
end if;
 
 
return	cd_procedimento_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
