CREATE OR REPLACE FUNCTION obter_cod_severidade_by_desc ( ds_severidade_p text ) RETURNS varchar AS $body$
DECLARE


	nr_gravidade_w cpoe_justif_interacao.nr_gravidade%type;


BEGIN
	if (ds_severidade_p IS NOT NULL AND ds_severidade_p::text <> '') then
		CASE ds_severidade_p
			WHEN 'MINOR' THEN
				nr_gravidade_w := 0;
			WHEN 'CONTRAINDICATED' THEN
				nr_gravidade_w := 1;
			WHEN 'MAJOR' THEN
				nr_gravidade_w := 2;
			WHEN 'MODERATE' THEN
				nr_gravidade_w := 3;
			WHEN 'MODERATE' THEN
				nr_gravidade_w := 4;
			WHEN 'INFO' THEN
				nr_gravidade_w := 5;
			WHEN 'INFORMATIVE' THEN
				nr_gravidade_w := 5;
			WHEN 'SUBTHERAPEUTIC' THEN
				nr_gravidade_w := 3;
			WHEN 'OVERDOSE' THEN
				nr_gravidade_w := 2;
			WHEN 'LEVEL_1' THEN
				nr_gravidade_w := 0;
			WHEN 'LEVEL_2' THEN
				nr_gravidade_w := 3;
			WHEN 'LEVEL_3' THEN
				nr_gravidade_w := 1;
			WHEN 'LEVEL_4' THEN
				nr_gravidade_w := 2;	
			ELSE
				nr_gravidade_w := 99;
		END CASE;
	end if;

	return nr_gravidade_w;

end;
