CREATE OR REPLACE FUNCTION obter_cod_usuario_atend_glosa ( nr_interno_conta_p bigint, ie_cod_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
nr_seq_interno_w	bigint;
cd_usuario_convenio_w	varchar(40);
cd_usuario_conv_glosa_w	varchar(40);
dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
nr_atendimento_w	bigint;
cd_convenio_conta_w	integer;
cd_convenio_glosa_w	integer;


BEGIN 
 
select	dt_periodo_inicial, 
	dt_periodo_final, 
	nr_atendimento, 
	cd_convenio_parametro 
into STRICT	dt_periodo_inicial_w, 
	dt_periodo_final_w, 
	nr_atendimento_w, 
	cd_convenio_conta_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
if (coalesce(ie_cod_usuario_p,'N') = 'S') then 
	 
	select	coalesce(max(nr_seq_interno),0) 
	into STRICT	nr_seq_interno_w 
	from	atend_categoria_convenio 
	where	nr_atendimento		= nr_atendimento_w 
	and	dt_inicio_vigencia	= (	SELECT	max(dt_inicio_vigencia) 
						from	atend_categoria_convenio 
						where	nr_atendimento	= nr_atendimento_w 
						and	coalesce(dt_inicio_vigencia, clock_timestamp()) between coalesce(dt_periodo_inicial_w, clock_timestamp()) and coalesce(dt_periodo_final_w, clock_timestamp()));
						 
	if (nr_seq_interno_w <> 0) then 
		select	max(cd_usuario_convenio), 
			max(cd_usuario_conv_glosa), 
			max(cd_convenio_glosa) 
		into STRICT	cd_usuario_convenio_w, 
			cd_usuario_conv_glosa_w, 
			cd_convenio_glosa_w 
		from	atend_categoria_convenio	 
		where	nr_seq_interno = nr_seq_interno_w;
		 
		if (coalesce(cd_convenio_glosa_w,0) = cd_convenio_conta_w) then 
			cd_usuario_convenio_w	:= coalesce(cd_usuario_conv_glosa_w,cd_usuario_convenio_w);
		end if;		
	end if;
						 
else 
 
	select	coalesce(max(nr_seq_interno),0) 
	into STRICT	nr_seq_interno_w 
	from	atend_categoria_convenio 
	where	nr_atendimento		= nr_atendimento_w 
	and	dt_inicio_vigencia	= (	SELECT	max(dt_inicio_vigencia) 
						from	atend_categoria_convenio 
						where	nr_atendimento	= nr_atendimento_w);
						 
	if (nr_seq_interno_w <> 0) then 
		select	max(cd_usuario_convenio), 
			max(cd_usuario_conv_glosa), 
			max(cd_convenio_glosa) 
		into STRICT	cd_usuario_convenio_w, 
			cd_usuario_conv_glosa_w, 
			cd_convenio_glosa_w 
		from	atend_categoria_convenio	 
		where	nr_seq_interno = nr_seq_interno_w;
		 
		if (coalesce(cd_convenio_glosa_w,0) = cd_convenio_conta_w) then 
			cd_usuario_convenio_w	:= coalesce(cd_usuario_conv_glosa_w,cd_usuario_convenio_w);
		end if;		
	end if;
 
end if;
 
if (coalesce(cd_usuario_convenio_w::text, '') = '') then 
 
	select	max(cd_usuario_convenio) 
	into STRICT	cd_usuario_convenio_w 
	from	atend_categoria_convenio 
	where	nr_atendimento		= nr_atendimento_w 
	and	dt_inicio_vigencia	= (	SELECT	max(dt_inicio_vigencia) 
						from	atend_categoria_convenio 
						where	nr_atendimento	= nr_atendimento_w);
 
end if;
 
return	cd_usuario_convenio_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
