CREATE OR REPLACE FUNCTION obter_cod_usuario_conv ( nr_atendimento_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then

	select 		max(a.cd_usuario_convenio)
	into STRICT		ds_retorno_w
	from 		atend_categoria_convenio a
	where 		a.nr_atendimento = nr_atendimento_p
	and   		a.cd_convenio = cd_convenio_p
	and   		a.dt_inicio_vigencia = (SELECT  max(b.dt_inicio_vigencia)
						from 	atend_categoria_convenio b
						where 	b.nr_atendimento = a.nr_atendimento
						and     b.cd_convenio = a.cd_convenio);

end if;

return	ds_retorno_w;

end;
