CREATE OR REPLACE FUNCTION obter_cod_usuario_evol_atend (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(5);
ie_tipo_evol_usuario_w  varchar(5);


BEGIN

select 	coalesce(max(ie_tipo_evolucao),'')
into STRICT	ie_tipo_evol_usuario_w
from 	usuario
where 	nm_usuario = nm_usuario_p;

if (ie_tipo_evol_usuario_w = '4') then
	ds_retorno_w := 'N';
elsif (ie_tipo_evol_usuario_w = '1') then
	ds_retorno_w:= 'M';
elsif (ie_tipo_evol_usuario_w = '3') then
	ds_retorno_w:= 'E';
elsif (ie_tipo_evol_usuario_w = '5') then
	ds_retorno_w:= 'P';
elsif (ie_tipo_evol_usuario_w = '10') then
	ds_retorno_w:= 'FI';
elsif (ie_tipo_evol_usuario_w = '8') then
	ds_retorno_w:= 'F';
elsif (ie_tipo_evol_usuario_w = '7') then
		ds_retorno_w:= 'N';
else
	ds_retorno_w:= '';
end if;


return	ds_retorno_w;

end;
$body$
