CREATE OR REPLACE FUNCTION obter_column_name_grid_cluster ( nm_tabela_p text, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nm_coluna_w	varchar(30);


BEGIN

select	column_name
into STRICT	nm_coluna_w
from	user_tab_columns
where	table_name = nm_tabela_p
and	substr(column_name,4,length(column_name)) = nr_sequencia_p
and	column_id > 3;

return	nm_coluna_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
