CREATE OR REPLACE FUNCTION obter_coluna_fluxo_mensal ( qt_dia_p bigint, dt_mes_ref_p timestamp, dt_atual_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_dia_util_w		bigint;
qt_dia_util_anterior_w	bigint;
qt_dia_atual_w		bigint;
ie_dia_util_w		varchar(1);
ds_coluna_w		varchar(10);
ie_passado_real_w	varchar(1);


BEGIN

select	(to_char(obter_dia_util_mes(cd_estabelecimento_p,dt_mes_ref_p,qt_dia_p),'dd'))::numeric
into STRICT	qt_dia_util_w
;

select	max(a.ie_passado_real)
into STRICT	ie_passado_real_w
from	parametro_fluxo_caixa a
where	a.cd_estabelecimento	= cd_estabelecimento_p;

if (ie_passado_real_w = 'S') then

	select	substr(obter_se_dia_util(dt_atual_p,cd_estabelecimento_p),1,255)
	into STRICT	ie_dia_util_w
	;

	qt_dia_atual_w	:= (to_char(dt_atual_p,'dd'))::numeric;

	if (to_char(qt_dia_util_w)	= to_char(dt_atual_p,'dd')) then
		ds_coluna_w	:= qt_dia_util_w || ' P';
	else
		select	(to_char(obter_dia_util_mes(cd_estabelecimento_p,dt_mes_ref_p,qt_dia_p - 1),'dd'))::numeric
		into STRICT	qt_dia_util_anterior_w
		;

		if (qt_dia_util_anterior_w = qt_dia_atual_w) then
			ds_coluna_w	:= qt_dia_util_anterior_w || ' R';
		elsif (qt_dia_util_anterior_w > qt_dia_atual_w) then
			ds_coluna_w	:= qt_dia_util_anterior_w;
		else
			ds_coluna_w	:= qt_dia_util_w;
		end if;
	end if;

else

	ds_coluna_w	:= qt_dia_util_w;

end if;

RETURN ds_coluna_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
