CREATE OR REPLACE FUNCTION obter_coluna_fluxo (dt_inicial_p timestamp, dt_final_p timestamp, qt_coluna_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN

if	((trunc(dt_inicial_p, 'dd') + qt_coluna_p) = (trunc(dt_final_p, 'dd') + 1)) then
	ds_retorno_w		:= 'Total';
elsif	((trunc(dt_inicial_p, 'dd') + qt_coluna_p) <= trunc(dt_final_p, 'dd')) then
	ds_retorno_w		:= to_char(trunc(dt_inicial_p, 'dd') + qt_coluna_p, 'dd/mm');
else
	ds_retorno_w		:= '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
