CREATE OR REPLACE FUNCTION obter_comentario_externo_lab (cd_comentario_ext_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);


BEGIN

    if (cd_comentario_ext_p IS NOT NULL AND cd_comentario_ext_p::text <> '') then
        select	rtrim(xmlagg(XMLELEMENT(name e, ds_comment || ', ') order by ds_comment).extract('//text()'), ', ')
        into STRICT	ds_retorno_w
        from ( SELECT distinct ds_comment
                from CPOE_STD_COMMENT 
                where CD_EXTERNAL = cd_comentario_ext_p
                and coalesce(ie_situacao,'A') = 'A') alias6;
    end if;

    return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
