SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_complemento_prescr_opm (nr_prescricao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_retorno_aux_w	varchar(1000);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(284963, null, wheb_usuario_pck.get_nr_seq_idioma);--Cinta abdominal elástica
expressao2_w	varchar(255) := obter_desc_expressao_idioma(294770, null, wheb_usuario_pck.get_nr_seq_idioma);--Ombreira
expressao3_w	varchar(255) := obter_desc_expressao_idioma(289417, null, wheb_usuario_pck.get_nr_seq_idioma);--Espaldeira
expressao4_w	varchar(255) := obter_desc_expressao_idioma(293176, null, wheb_usuario_pck.get_nr_seq_idioma);--Meias elásticas (média compressão)
C01 CURSOR FOR
	SELECT expressao1_w

	from    PRESCR_COMPL_OPM a
	where   a.nr_prescricao = nr_prescricao_p
	and     a.ie_cinta  = 'S'
	
union

	SELECT expressao2_w

	from    PRESCR_COMPL_OPM a
	where   a.nr_prescricao = nr_prescricao_p
	and     a.IE_OMBREIRA  = 'S'
	
union

	select expressao3_w

	from    PRESCR_COMPL_OPM a
	where   a.nr_prescricao = nr_prescricao_p
	and     a.ie_espaldeira  = 'S'
	
union

	select expressao4_w

	from    PRESCR_COMPL_OPM a
	where   a.nr_prescricao = nr_prescricao_p
	and     a.ie_meias  = 'S';

BEGIN

open C01;
loop
fetch C01 into
	ds_retorno_aux_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_retorno_aux_w;
	else
		ds_retorno_w := ds_retorno_w ||', '|| ds_retorno_aux_w;
	end if;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
