SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_complexidade_conta (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_complexidade_w		varchar(1);
qt_passagem_uti_w		bigint;
qt_passagem_cc_w		bigint;


BEGIN

select	coalesce(sum(CASE WHEN cd_classif_setor=4 THEN 1  ELSE 0 END ),0),
		coalesce(sum(CASE WHEN cd_classif_setor=2 THEN 1  ELSE 0 END ),0)
into STRICT		qt_passagem_uti_w,
		qt_passagem_cc_w
from	Setor_atendimento c,
	Atend_Paciente_unidade b,
	Conta_Paciente a
where	b.cd_setor_atendimento	= c.cd_setor_atendimento
  and	a.nr_atendimento		= b.nr_atendimento
  and a.nr_interno_conta	= nr_interno_conta_p;

if (qt_passagem_uti_w > 0) then
	ie_complexidade_w		:= 'A';
elsif (qt_passagem_cc_w > 0) then
	ie_complexidade_w		:= 'M';
else
	ie_complexidade_w		:= 'B';
end if;

Return ie_complexidade_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
