SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_complexidade_nut (nr_atendimento_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text, ie_campo_p text default '1') RETURNS varchar AS $body$
DECLARE

			
ds_descricao_w			varchar(255);
ie_complexidade_nut_w		varchar(10);
nr_seq_score_flex_w		bigint;
nr_seq_score_flex_ii_w		bigint;
ds_result_w			varchar(255);
dt_avaliacao_w          		varchar(255);
ds_result_ii_w			varchar(255);
dt_avaliacao_ii_w         		varchar(255);


ie_escala_1_w				varchar(15);
ds_escala_1_w				varchar(255);
ie_escala_2_w				varchar(15);
ds_escala_2_w				varchar(255);
nr_seq_score_flex_ca1_w 	bigint;
ds_score_flex_1_w			varchar(255);
nr_seq_score_flex_ca2_w		bigint;
ds_score_flex_2_w			varchar(255);



BEGIN
	begin
	
	
	if (coalesce(ie_campo_p, 1) = 1)then
	
		select	coalesce(max(ie_complexidade_nut), 'N'),
				max(nr_seq_score_flex),
				max(substr(obter_valor_dominio(4037, IE_COMPLEXIDADE_NUT ),1,255)),
				max(substr(obter_desc_regra_score_flex(NR_SEQ_SCORE_FLEX,346),1,255))
		into STRICT	ie_complexidade_nut_w,
				nr_seq_score_flex_w,
				ds_escala_1_w,
				ds_score_flex_1_w
		from	setor_atendimento
		where	cd_setor_atendimento	= cd_setor_atendimento_p;
		
	elsif (coalesce(ie_campo_p, 1) = 2)then
	
		select	coalesce(max(ie_complexidade_nut2), 'N'),
				max(nr_seq_score_flex_ii),
				max(substr(obter_valor_dominio(4037,IE_COMPLEXIDADE_NUT2),1,255)),
				max(substr(obter_desc_regra_score_flex(NR_SEQ_SCORE_FLEX_II,346),1,255))
		into STRICT	ie_complexidade_nut_w,
				nr_seq_score_flex_ii_w,
				ds_escala_2_w,
				ds_score_flex_2_w
		from	setor_atendimento
		where	cd_setor_atendimento	= cd_setor_atendimento_p;
		
	end if;

	if ( ie_complexidade_nut_w	= 'N')then
		
		if (ie_opcao_p	in ('P','G','PC')) then
			select	substr(Obter_Resul_nrs(qt_pontuacao),1,255) || ' - ' || to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
			into STRICT	ds_descricao_w
			from	ESCALA_NRS a
			where	nr_sequencia	= (	SELECT	max(x.nr_sequencia)
							from	ESCALA_NRS x
							where	x.nr_atendimento	= nr_atendimento_p
							and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
							and	coalesce(x.dt_inativacao::text, '') = '');
		end if;
	elsif (ie_complexidade_nut_w = 'M')then
		select	substr(obter_desc_result_mst(qt_pontuacao),1,255) || ' - ' || to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_descricao_w
		from	escala_mst
		where	nr_sequencia = (	SELECT	max(x.nr_sequencia)
						from	escala_mst x
						where	x.nr_atendimento = nr_atendimento_p
						and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
						and	coalesce(x.dt_inativacao::text, '') = '');
	elsif (ie_complexidade_nut_w = 'S')then

		select	substr(obter_result_escala_strong(qt_ponto) || ' - ' || to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss'),1,255)
		into STRICT	ds_descricao_w
		from	escala_strong_kids
		where	nr_sequencia = (	SELECT	max(x.nr_sequencia)
						from	escala_strong_kids x
						where	x.nr_atendimento = nr_atendimento_p
						and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
						and	coalesce(x.dt_inativacao::text, '') = '');

   elsif (ie_complexidade_nut_w = 'MNAS') then

    select SUBSTR(Obter_Result_Man_sf(qt_pontuacao),1,80)
    into STRICT ds_descricao_w
    from escala_man_lf
    where nr_sequencia = (SELECT	max(x.nr_sequencia)
						from	escala_man_lf x
						where	x.nr_atendimento = nr_atendimento_p
						and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
						and	coalesce(x.dt_inativacao::text, '') = '');

	elsif (ie_complexidade_nut_w = 'E')  then
	
		select	substr(obter_result_escala_sist_nut(ie_dietoterapia,ie_fator_risco),1,255) || ' - ' || to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
		into STRICT	ds_descricao_w
		from	ESCALA_SIST_NUTRICIONAL
		where	nr_sequencia = (	SELECT	max(x.nr_sequencia)
						from	ESCALA_SIST_NUTRICIONAL x
						where	x.nr_atendimento = nr_atendimento_p
						and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
						and	coalesce(x.dt_inativacao::text, '') = '');
						
	elsif (ie_complexidade_nut_w = 'SF')  then
	
		if (coalesce(ie_campo_p, 1) = 1) then

			if (nr_seq_score_flex_w IS NOT NULL AND nr_seq_score_flex_w::text <> '') then

				select	substr(obter_resultado_escala_eif(nr_sequencia,'D'),1,255),
						to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				into STRICT 	ds_result_w,
						dt_avaliacao_w
				from 	escala_eif
				where	nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif x
											where	x.nr_atendimento = nr_atendimento_p
											and		x.nr_seq_escala = nr_seq_score_flex_w
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		coalesce(x.dt_inativacao::text, '') = '');

			else
		
				select	substr(obter_resultado_escala_eif(nr_sequencia,'D'),1,255),
						to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				into STRICT 	ds_result_w,
						dt_avaliacao_w
				from 	escala_eif
				where	nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif x
											where	x.nr_atendimento = nr_atendimento_p
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		coalesce(x.dt_inativacao::text, '') = '');
										
			end if;
						
			if (ds_result_w IS NOT NULL AND ds_result_w::text <> '')	then
				ds_descricao_w := ds_result_w || ' - ' || dt_avaliacao_w;
			end if;
			
		elsif (coalesce(ie_campo_p, 1) = 2) then		
		
			if (nr_seq_score_flex_ii_w IS NOT NULL AND nr_seq_score_flex_ii_w::text <> '') then

				select	substr(obter_resultado_escala_eif(nr_sequencia,'D'),1,255),
						to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				into STRICT 	ds_result_w,
						dt_avaliacao_w
				from 	escala_eif
				where	nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif x
											where	x.nr_atendimento = nr_atendimento_p
											and		x.nr_seq_escala = nr_seq_score_flex_ii_w
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		coalesce(x.dt_inativacao::text, '') = '');

			else
		
				select	substr(obter_resultado_escala_eif(nr_sequencia,'D'),1,255),
						to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				into STRICT 	ds_result_w,
						dt_avaliacao_w
				from 	escala_eif
				where	nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif x
											where	x.nr_atendimento = nr_atendimento_p
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		coalesce(x.dt_inativacao::text, '') = '');
										
			end if;
						
			if (ds_result_w IS NOT NULL AND ds_result_w::text <> '')	then
				ds_descricao_w := ds_result_w || ' - ' || dt_avaliacao_w;
			end if;
			
			
		end if;
						
	elsif (ie_complexidade_nut_w = 'SFII')  then
	
		if (coalesce(ie_campo_p, 1) = 1) then

			if (nr_seq_score_flex_w IS NOT NULL AND nr_seq_score_flex_w::text <> '') then
			
				 select	 substr(obter_desc_resul_score_flex_2(qt_pontos,nr_seq_escala),1,255),
						 to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				 into STRICT 	 ds_result_ii_w,
						 dt_avaliacao_ii_w
				 from 	 escala_eif_ii
				 where	 nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif_ii x
											where	x.nr_atendimento = nr_atendimento_p
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		x.nr_seq_escala = nr_seq_score_flex_w
											and		coalesce(x.dt_inativacao::text, '') = '');	
			
			
			else
			
			
				 select	 substr(obter_desc_resul_score_flex_2(qt_pontos,nr_seq_escala),1,255),
						 to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				 into STRICT 	 ds_result_ii_w,
						 dt_avaliacao_ii_w
				 from 	 escala_eif_ii
				 where	 nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif_ii x
											where	x.nr_atendimento = nr_atendimento_p
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		coalesce(x.dt_inativacao::text, '') = '');			
			
			end if;
			
			
						
			if (ds_result_ii_w IS NOT NULL AND ds_result_ii_w::text <> '')	then
				ds_descricao_w := ds_result_ii_w || ' - ' || dt_avaliacao_ii_w;
			end if;
			
			
		elsif (coalesce(ie_campo_p, 1) = 2) then	
		
			if (nr_seq_score_flex_ii_w IS NOT NULL AND nr_seq_score_flex_ii_w::text <> '') then
			
				 select	 substr(obter_desc_resul_score_flex_2(qt_pontos,nr_seq_escala),1,255),
						 to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				 into STRICT 	 ds_result_ii_w,
						 dt_avaliacao_ii_w
				 from 	 escala_eif_ii
				 where	 nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif_ii x
											where	x.nr_atendimento = nr_atendimento_p
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		x.nr_seq_escala = nr_seq_score_flex_ii_w
											and		coalesce(x.dt_inativacao::text, '') = '');	
			
			
			else
			
			
				 select	 substr(obter_desc_resul_score_flex_2(qt_pontos,nr_seq_escala),1,255),
						 to_char(dt_avaliacao,'dd/mm/yyyy hh24:mi:ss')
				 into STRICT 	 ds_result_ii_w,
						 dt_avaliacao_ii_w
				 from 	 escala_eif_ii
				 where	 nr_sequencia = (	SELECT	max(x.nr_sequencia)
											from	escala_eif_ii x
											where	x.nr_atendimento = nr_atendimento_p
											and		(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
											and		coalesce(x.dt_inativacao::text, '') = '');			
			
			end if;
			
			
						
			if (ds_result_ii_w IS NOT NULL AND ds_result_ii_w::text <> '')	then
			
				ds_descricao_w := ds_result_ii_w || ' - ' || dt_avaliacao_ii_w;
				
			end if;
	
	
						
		end if;
	end if;
	exception
	when others then
		ds_descricao_w := '';
	end;
	
	
if ( ie_opcao_p = 'PC' ) and (ds_descricao_w IS NOT NULL AND ds_descricao_w::text <> '') then
	
	if (coalesce(ie_campo_p, 1) = 1) then

		ds_descricao_w := substr(coalesce(ds_score_flex_1_w,ds_escala_1_w)||': '|| ds_descricao_w,1,255);
	
	else
	
		ds_descricao_w := substr(coalesce(ds_score_flex_2_w,ds_escala_2_w)||': '|| ds_descricao_w,1,255);
	
	end if;

end if;
	


return	ds_descricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_complexidade_nut (nr_atendimento_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text, ie_campo_p text default '1') FROM PUBLIC;

