SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_complicacoes_cirurgia (nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1000);
ie_primeira_vez_w  varchar(1):= 'S';

c_complicacao CURSOR FOR			
	SELECT  obter_valor_dominio(9747, cd_complicacao) ds_complicacao,
			substr(ds_observacao,1,500) ds_observacao
	from anestesia_complicacoes
	where coalesce(ie_situacao, 'A') = 'A'
	and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and nr_cirurgia = nr_cirurgia_p;

BEGIN
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then

	<<read_complicacao>>
	for r_complicacao in c_complicacao
	loop
		if (ie_primeira_vez_w = 'S') then
			ds_retorno_w:= OBTER_DESC_EXPRESSAO(974139) || ': ';
			ie_primeira_vez_w:= 'N';
		end if;
		ds_retorno_w:= ds_retorno_w || r_complicacao.ds_complicacao;
		if (r_complicacao.ds_observacao IS NOT NULL AND r_complicacao.ds_observacao::text <> '') then
			ds_retorno_w:= ds_retorno_w || ' - ' || r_complicacao.ds_observacao;
		end if;
		ds_retorno_w:= ds_retorno_w || chr(13) || chr(10);
   end loop read_complicacao;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_complicacoes_cirurgia (nr_cirurgia_p bigint) FROM PUBLIC;

