CREATE OR REPLACE FUNCTION obter_complitude_clepa (nr_checklist_p bigint, nr_avaliacao_p bigint) RETURNS bigint AS $body$
DECLARE

nr_retorno_w double precision;
qt_liberada_w bigint;
qt_nao_liberada bigint;

BEGIN
IF (nr_checklist_p IS NOT NULL AND nr_checklist_p::text <> '') THEN
	SELECT  COUNT(DISTINCT a.nr_seq_checklist_item)
	INTO STRICT 	qt_liberada_w
	FROM 	med_avaliacao_paciente a
	WHERE 	a.nr_seq_atend_checklist = nr_checklist_p
	AND 	((coalesce(nr_avaliacao_p,0) = 0) OR ((coalesce(nr_avaliacao_p,0) > 0) AND (a.nr_seq_checklist_item = nr_avaliacao_p)))
	AND 	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	AND NOT EXISTS (SELECT	1
		FROM    med_avaliacao_paciente b
		WHERE	b.nr_seq_atend_checklist = a.nr_seq_atend_checklist
		AND	b.nr_seq_checklist_item = a.nr_seq_checklist_item
		AND	coalesce(b.dt_liberacao::text, '') = '');

 	SELECT  COUNT(DISTINCT a.NR_SEQ_CHECKLIST_ITEM)
	into STRICT	qt_nao_liberada
	FROM 	med_avaliacao_paciente a
	WHERE 	a.nr_seq_atend_checklist = nr_checklist_p
	AND 	((coalesce(nr_avaliacao_p,0) = 0) OR ((coalesce(nr_avaliacao_p,0) > 0) AND (a.nr_seq_checklist_item = nr_avaliacao_p)))
	AND 	coalesce(a.dt_liberacao::text, '') = '';

	nr_retorno_w := coalesce(clepa_obter_perc_valor(qt_liberada_w,qt_liberada_w + qt_nao_liberada),0);
END IF;
RETURN nr_retorno_w;
END;
