SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_compl_cid (nr_atendimento_p bigint, dt_diagnostico_p timestamp, cd_doenca_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10000);
ds_diagnostico_w		varchar(2000);
ie_classificacao_doenca_w	varchar(60);
ie_tipo_doenca_w		varchar(60);
ie_unidade_tempo_w		varchar(10);
qt_tempo_w			bigint;
ie_lado_w			varchar(60);
dt_manifestacao_w		timestamp;


BEGIN

select	ds_diagnostico,
	substr(obter_valor_dominio(58,ie_classificacao_doenca),1,60),
	substr(obter_valor_dominio(1758,ie_tipo_doenca),1,60),
	ie_unidade_tempo,
	qt_tempo,
	substr(obter_valor_dominio(1372,ie_lado),1,60),
	dt_manifestacao
into STRICT	ds_diagnostico_w,
	ie_classificacao_doenca_w,
	ie_tipo_doenca_w,
	ie_unidade_tempo_w,
	qt_tempo_w,
	ie_lado_w,
	dt_manifestacao_w
from	diagnostico_doenca
where	nr_atendimento	= nr_atendimento_p
and	dt_diagnostico	= dt_diagnostico_p
and	cd_doenca	= cd_doenca_p;

if (dt_manifestacao_w IS NOT NULL AND dt_manifestacao_w::text <> '') then
	begin
	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(308867) || ': ' /*'Manifestação: '*/||to_char(dt_manifestacao_w,'dd/mm/yyyy') ||chr(13) ||chr(10);
	end;
end if;

if (ie_classificacao_doenca_w IS NOT NULL AND ie_classificacao_doenca_w::text <> '') then
	begin
	ds_retorno_w	:= ds_retorno_w || Wheb_mensagem_pck.get_texto(308868) || ': ' /*'Classificação: '*/||ie_classificacao_doenca_w ||chr(13) ||chr(10);
	end;
end if;

if (ie_tipo_doenca_w IS NOT NULL AND ie_tipo_doenca_w::text <> '') then
	begin
	ds_retorno_w	:= ds_retorno_w || Wheb_mensagem_pck.get_texto(308869) || ': ' /*'Tipo: '*/||ie_tipo_doenca_w ||chr(13) ||chr(10);
	end;
end if;

if (ie_unidade_tempo_w IS NOT NULL AND ie_unidade_tempo_w::text <> '') and (coalesce(qt_tempo_w,0) > 0) then
	begin
	ds_retorno_w	:= ds_retorno_w || Wheb_mensagem_pck.get_texto(308870) || ': ' /*'Tempo doença: '*/||to_char(qt_tempo_w) || ' '|| ie_unidade_tempo_w ||chr(13) ||chr(10);
	end;
end if;

if (ie_lado_w IS NOT NULL AND ie_lado_w::text <> '') then
	begin
	ds_retorno_w	:= ds_retorno_w || Wheb_mensagem_pck.get_texto(308871) || ': ' /*'Lado: '*/||ie_lado_w ||chr(13) ||chr(10);
	end;
end if;

if (ds_diagnostico_w IS NOT NULL AND ds_diagnostico_w::text <> '') then
	begin
	ds_retorno_w	:= ds_retorno_w || Wheb_mensagem_pck.get_texto(308872) || ': ' /*'Observação: '*/||ds_diagnostico_w ||chr(13) ||chr(10);
	end;
end if;

return	substr(ds_retorno_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
