CREATE OR REPLACE FUNCTION obter_compl_desc_mat_lab ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

qt_amostra_w		smallint;
nr_sequencia_w	bigint;
qt_material_w		integer := 0;
ds_retorno_w	varchar(20);
C01 CURSOR FOR
	SELECT nr_sequencia
	from prescr_proc_material
	where nr_prescricao	= nr_prescricao_p
	  and nr_seq_material	= nr_seq_material_p
	order by 1;

BEGIN
select	count(*)
into STRICT	qt_amostra_w
from	prescr_proc_material
where nr_prescricao	= nr_prescricao_p
  and nr_seq_material	= nr_seq_material_p;
ds_retorno_w := '';
if (qt_amostra_w	> 1) then
	open C01;
	loop
		fetch C01 into nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		qt_material_w := qt_material_w + 1;
		if (nr_sequencia_w = nr_sequencia_p) then
			ds_retorno_w	:= to_char(qt_material_w) || 'Âª ' || obter_desc_expressao(283435);
		end if;
	end loop;
	close c01;
end if;

if (qt_material_w <= 1) then
	ds_retorno_w := null;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
