SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_compl_orient_checkup (nr_seq_orientacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_complemento_w	varchar(15) := '';
qt_complemento_w	bigint;


BEGIN
if (nr_seq_orientacao_p IS NOT NULL AND nr_seq_orientacao_p::text <> '') then
	select	coalesce(count(*),0)
	into STRICT	qt_complemento_w
	from	checkup_diagnostico c,
		checkup_diag_pac_orient b,
		checkup_orientacao a
	where	c.nr_sequencia		= b.nr_seq_diag
	and	c.nr_atendimento	= a.nr_atendimento
	and	b.ie_relatorio		= 'S'
	and	b.nr_seq_orientacao	= a.nr_seq_orientacao
	and	a.nr_sequencia		= nr_seq_orientacao_p
	and	c.ie_situacao		= 'A'
	and	b.ie_situacao		= 'A';

	if (coalesce(qt_complemento_w,0) > 0) then
		ds_complemento_w	:= Wheb_mensagem_pck.get_texto(308873); --', relativo a:';
	end if;
end if;

RETURN	ds_complemento_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
