SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_componentes_sol_rotina (cd_protocolo_p bigint, nr_seq_solucao_p bigint, nr_sequencia_p bigint, ie_quebra_linha_p text) RETURNS varchar AS $body$
DECLARE


ds_componentes_w	varchar(500);
ie_separador_w		varchar(3);

c01 CURSOR FOR
SELECT obter_desc_material(cd_material) ds_material,
       a.qt_dose,
	   b.ds_unidade_medida
  from protocolo_medic_material a,
       unidade_medida b
 where a.cd_protocolo = cd_protocolo_p
   and a.nr_seq_solucao = nr_seq_solucao_p
   and a.nr_sequencia = nr_sequencia_p
   and b.cd_unidade_medida = a.cd_unidade_medida
order by
	ds_material;

BEGIN

ie_separador_w := chr(13)||chr(10);

if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if (ie_quebra_linha_p = 'N') then
		ie_separador_w := ',';
	end if;	
	
	for r_c01_w in c01
	loop
		if (length(ds_componentes_w || r_c01_w.ds_material || ' ' || to_char(r_c01_w.qt_dose) || ' ' || r_c01_w.ds_unidade_medida || ie_separador_w) <= 255) then
			ds_componentes_w := substr(ds_componentes_w || r_c01_w.ds_material || ' ' || to_char(r_c01_w.qt_dose) || ' ' || r_c01_w.ds_unidade_medida || ie_separador_w, 1, 255);
		end if;				
	end loop;	
end if;

return ds_componentes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
