SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_componentes_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_quebra_linha_p text, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dose_w		double precision;
cd_unidade_medida_w	varchar(30);
ds_material_w		varchar(80);
ds_componentes_w	varchar(2000);
ie_separador_w		varchar(3);
ds_dose_w		varchar(50);
ds_observacao_w		varchar(255);
ie_exibir_obs_w		varchar(1) := 'N';
qt_dias_util_w		smallint;

c01 CURSOR FOR
SELECT	a.qt_dose,
	a.cd_unidade_medida_dose,
	substr(b.ds_reduzida,1,80) ds_material
from	material b,
	prescr_material a
where	a.nr_prescricao		= nr_prescricao_p
and	a.nr_sequencia_solucao	= nr_seq_solucao_p
and	a.cd_material		= b.cd_material
/*and	a.dt_suspensao		is null 	OS 338976*/

order by
	ds_material;


BEGIN

ie_separador_w := chr(13)||chr(10);

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	if (ie_quebra_linha_p = 'N') then
		ie_separador_w := ',';
	end if;
	open c01;
	loop
	fetch c01 into	qt_dose_w,
			cd_unidade_medida_w,
			ds_material_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_dose_w	:= to_char(qt_dose_w);
		if (substr(to_char(qt_dose_w),1,1) = ',') then
			ds_dose_w	:= '0'||to_char(qt_dose_w);
		end if;
		ds_componentes_w := ds_componentes_w || ds_material_w || ' ' || ds_dose_w || ' ' || cd_unidade_medida_w || ie_separador_w;
		end;
	end loop;
	close c01;
end if;

return ds_componentes_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
