CREATE OR REPLACE FUNCTION obter_componente_sv (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(5);


BEGIN
ds_retorno_w := '';
if (nr_sequencia_p > 0) then

	select     max(ie_componente)
	into STRICT	   ds_retorno_w
	from	   tabela_atributo b,
			   pepo_sv a
	where	   a.nm_tabela 	   = b.nm_tabela
	and		   a.nm_atributo   = b.nm_atributo
	and		   a.nr_sequencia  = nr_sequencia_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
