SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_comprador_material ( cd_material_p bigint, ie_urgente_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
qt_regra_w				integer;
cd_comprador_w				varchar(10);
ie_comprador_w				bigint;
nr_sequencia_w				bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	sup_regra_resp_compras
where	coalesce(cd_grupo_material, cd_grupo_material_w)     		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)		= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_w)			= cd_classe_material_w
and (coalesce(cd_material, cd_material_p)				= cd_material_p or cd_material_p = 0)
and	coalesce(cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p
and	((ie_urgente = 'T') or (ie_urgente = ie_urgente_p))
order by	coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material,0),
	coalesce(cd_grupo_material,0),
	coalesce(ie_urgente,'T');


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	sup_regra_resp_compras;

if (qt_regra_w > 0) then
	begin

	select	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from	estrutura_material_v
	where	cd_material = cd_material_p;

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;

	if (coalesce(nr_sequencia_w,0) > 0) then
		begin

		select	coalesce(max(cd_comprador),0)
		into STRICT	cd_comprador_w
		from	sup_regra_resp_com_deleg
		where	nr_seq_regra = nr_sequencia_w
		and	dt_limite >= trunc(clock_timestamp());

		if (cd_comprador_w = 0) then
			begin

			select	cd_comprador
			into STRICT	cd_comprador_w
			from	sup_regra_resp_compras
			where	nr_sequencia = nr_sequencia_w;

			end;
		end if;

		end;
	end if;

	end;
end if;

return	cd_comprador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
