CREATE OR REPLACE FUNCTION obter_comprador_usuario ( nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_comprador_w		comprador.cd_pessoa_fisica%type;


BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_comprador_w 
from	comprador 
where	cd_pessoa_fisica = obter_pessoa_fisica_usuario(nm_usuario_p,'C') 
and	ie_situacao = 'A' 
and	cd_estabelecimento = cd_estabelecimento_p;
 
return	cd_comprador_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
