CREATE OR REPLACE FUNCTION obter_comunic_cliente_inadim (cd_cnpj_p text) RETURNS varchar AS $body$
DECLARE


ie_atualizar_versao_w	varchar(1);
ds_comunicado_w		FIN_EVENTO_COMUNICADO.DS_COMUNICADO%type;
ds_retorno_w		varchar(4000) := '';


BEGIN
	if (cd_cnpj_p IS NOT NULL AND cd_cnpj_p::text <> '') then
		begin
		select	max(coalesce(ie_atualizar_versao,null))
		into STRICT	ie_atualizar_versao_w
		from	fin_cliente_inadimplente
		where	cd_cnpj = cd_cnpj_p;

		if (ie_atualizar_versao_w IS NOT NULL AND ie_atualizar_versao_w::text <> '') then
			begin

			select	ds_comunicado
			into STRICT	ds_comunicado_w
			from	fin_evento_comunicado
			where	ie_evento = ie_atualizar_versao_w;

			ds_retorno_w :=  substr(ie_atualizar_versao_w || ';' || ds_comunicado_w,1,4000);

			end;
		end if;
		end;
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
