CREATE OR REPLACE FUNCTION obter_comunity_code ( cd_cep_p postal_community_code.cd_postal%TYPE ) RETURNS varchar AS $body$
DECLARE

    cd_gemeinde_code_w postal_community_code.cd_gemeinde_code%TYPE;

BEGIN
    SELECT DISTINCT
        coalesce(a.cd_gemeinde_code, 0)
    INTO STRICT cd_gemeinde_code_w
    FROM
        postal_community_code a
    WHERE
        1 = 1
        AND a.cd_postal = cd_cep_p  LIMIT 1;

    IF ( cd_gemeinde_code_w = '0' ) THEN
        RETURN '';
    END IF;
    RETURN cd_gemeinde_code_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
