CREATE OR REPLACE FUNCTION obter_concatenacao_crm_sigla ( cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


nr_crm_w varchar(20);
uf_crm_w varchar(15);


BEGIN

SELECT
  nr_crm,
  uf_crm
INTO STRICT 
  nr_crm_w,
  uf_crm_w
FROM 
  medico a
WHERE
  a.cd_pessoa_fisica = cd_pessoa_fisica_p;

RETURN concat(nr_crm_w, concat(' - ', uf_crm_w));

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
