SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_concat_comp_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_comp_w		bigint;
ds_componente_w		varchar(100);
ds_reduzida_w		varchar(100);
ds_material_w       varchar(100);

c01 CURSOR FOR
SELECT	substr(coalesce(b.ds_abrev_solucao,b.ds_reduzida), 1, 100)
from	prescr_material a,
		material b,
		prescr_medica c
where	a.nr_prescricao 		= c.nr_prescricao
and		a.nr_prescricao 		= nr_prescricao_p
and		a.nr_sequencia_solucao 	= nr_seq_solucao_p
and		a.cd_material			= b.cd_material
and 	not exists (SELECT c.nr_prescricao
					FROM prescr_material c
					WHERE c.nr_prescricao	= a.nr_prescricao
					and	c.nr_sequencia = a.nr_seq_substituto)
and		coalesce(c.cd_funcao_origem,924) <> 2314;			

c02 CURSOR FOR
SELECT	substr(coalesce(b.ds_abrev_solucao,b.ds_reduzida), 1, 100)
from	prescr_material a,
		material b,
		prescr_medica c
where	a.nr_prescricao 		= c.nr_prescricao
and		a.nr_prescricao 		= nr_prescricao_p
and		a.nr_sequencia_solucao 	= nr_seq_solucao_p
and		a.cd_material			= b.cd_material
and 	not exists (SELECT c.nr_prescricao 
					FROM prescr_material c
					WHERE c.nr_prescricao	= a.nr_prescricao
					and	c.nr_sequencia = a.nr_seq_substituto)
and		coalesce(c.cd_funcao_origem,924) = 2314				
order by ordena_item_cpoe(a.nr_seq_mat_cpoe, a.cd_material);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	
	open C01;
	loop
	fetch C01 into	
		ds_reduzida_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		if (ds_componente_w IS NOT NULL AND ds_componente_w::text <> '') then
			ds_componente_w	:= substr(ds_componente_w || ' + ' || ds_reduzida_w,1,100);
		else
			ds_componente_w	:= substr(ds_reduzida_w,1,100);
		end if;
	end loop;
	close C01;
	
	
	open C02;
	loop
	fetch C02 into	
		ds_reduzida_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		if (ds_componente_w IS NOT NULL AND ds_componente_w::text <> '') then
			ds_componente_w	:= substr(ds_componente_w || ' + ' || ds_reduzida_w,1,100);
		else
			ds_componente_w	:= substr(ds_reduzida_w,1,100);
		end if;
	end loop;
	close C02;
	
    select  substr(max(obter_desc_material(a.cd_material)), 1, 100)
    into STRICT    ds_material_w
    from    prescr_material a
    where   a.nr_prescricao         = nr_prescricao_p
    and     a.nr_sequencia_solucao 	= nr_seq_solucao_p;

end if;

return coalesce(ds_componente_w, ds_material_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_concat_comp_sol (nr_prescricao_p bigint, nr_seq_solucao_p bigint) FROM PUBLIC;

