SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_concat_patient_tag (nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


/* 
The output for this function should begin either
Empty string ''
Or
Sequence of tag codes separated by , and space
Example: 17, 28, 39, 46
*/
c01 CURSOR FOR
SELECT pct.ds_tag
from PAN_CONFIGURACAO_TAG pct
where pct.NR_SEQUENCIA in (
  SELECT apt.nr_sequencia_tag
  from atendimento_paciente_tag apt
  where apt.nr_atendimento = nr_atendimento_p
  and apt.cd_pessoa_fisica = cd_pessoa_fisica_p
  )
order by pct.nr_sequencia
;

lista_tags_w	varchar(4000);

BEGIN

if (coalesce(nr_atendimento_p::text, '') = '' or coalesce(cd_pessoa_fisica_p::text, '') = '') then
	lista_tags_w := null;
else
	for c01_row in c01 loop
	begin
	
	lista_tags_w := lista_tags_w || c01_row.ds_tag || ', ';
	
	end;
	end loop;
	
	/* Remove last , and space */

	lista_tags_w := substr(lista_tags_w, 0, length(lista_tags_w)-2);
end if;

return lista_tags_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
