CREATE OR REPLACE FUNCTION obter_conceito_aval_fornec ( cd_estabelecimento_p bigint, pr_percentual_p text) RETURNS varchar AS $body$
DECLARE


ie_conceito_w		varchar(1);


BEGIN

select	coalesce(max(ie_conceito_avaliacao), '0')
into STRICT	ie_conceito_w
from	regra_conceito_aval_fornec
where	cd_estabelecimento	= cd_estabelecimento_p
and	pr_percentual_p >= ie_faixa_minimo
and	pr_percentual_p <= ie_faixa_maximo;

RETURN	coalesce(ie_conceito_w, '0');

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
