CREATE OR REPLACE FUNCTION obter_concentracao_medic (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(40);
qt_conversao_mg_w		double precision;
cd_unid_med_concentracao_w	varchar(30);
cd_unid_med_base_conc_w		varchar(30);


BEGIN

select	qt_conversao_mg,
	cd_unid_med_concetracao,
	cd_unid_med_base_conc
into STRICT	qt_conversao_mg_w,
	cd_unid_med_concentracao_w,
	cd_unid_med_base_conc_w
from	material
where	cd_material = cd_material_p;

ds_retorno_w	:= campo_mascara_virgula(qt_conversao_mg_w)||cd_unid_med_concentracao_w||'/'||cd_unid_med_base_conc_w;

return ds_retorno_w;
end;
